import os
import sys

BASE_PATH = os.path.dirname(os.path.abspath(sys.argv[0]))
img_default = os.path.join(BASE_PATH,'src','default','FileNotFoundErr.png')
tags_path = os.path.join(BASE_PATH,'src','users-data','tags.csv')
templates_path = os.path.join(BASE_PATH, 'src','default','collage-templates')
err = b''
folder_icon = b''
file_icon = b''

def tosave(path):
	path_rel = os.path.relpath(path, start = BASE_PATH)
	path_gen = path_rel.replace(os.path.sep, "/")
	return path_gen

def toload(path):
	path_sys = path.replace("/", os.path.sep)
	path_abs = os.path.abspath(os.path.join(BASE_PATH, path_sys))
	return path_abs